import React, { useEffect, useState } from "react";
import axios from "axios";
import Sidebar from "../sidebar";
import Modal from "react-modal";
import "../../assets/Discussions.module.css";
import { useUser } from "../../context/UserContext"; // Import the UserContext


const DiscussionForum = () => {
    const { user } = useUser();
  const [threads, setThreads] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedThread, setSelectedThread] = useState(null);
  const [replyModalOpen, setReplyModalOpen] = useState(false);

  useEffect(() => {
    fetchThreads();
  }, []);

  const fetchThreads = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/threads");
      setThreads(response.data);
    } catch (error) {
      console.error("Error fetching threads:", error);
    }
  };

  const handleLikeThread = async (threadId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/threads/${threadId}/like`,
        {},
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      setThreads((prevThreads) =>
        prevThreads.map((thread) =>
          thread._id === threadId ? { ...thread, likes: response.data.thread.likes } : thread
        )
      );
    } catch (error) {
      console.error("Error liking thread:", error);
    }
  };

  const handleDislikeThread = async (threadId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/threads/${threadId}/dislike`,
        {},
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      setThreads((prevThreads) =>
        prevThreads.map((thread) =>
          thread._id === threadId ? { ...thread, dislikes: response.data.thread.dislikes } : thread
        )
      );
    } catch (error) {
      console.error("Error disliking thread:", error);
    }
  };

  const handleCreateThread = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/api/threads",
        { title, content },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      setThreads([response.data.thread, ...threads]);
      setTitle("");
      setContent("");
      setIsModalOpen(false);
    } catch (error) {
      console.error("Error creating thread:", error);
    }
  };

  return (
    <div className="flex">
      <Sidebar userType={user.userType} userId={user?._id}/>
      <div className="flex-1 p-6 ml-64">
        <div className="flex justify-between mb-4">
          <h1 className="text-2xl font-bold">Discussion Forum</h1>
          {user && (
            <button
              onClick={() => setIsModalOpen(true)}
              className="bg-blue-500 text-white px-4 py-2 rounded"
            >
              Create Discussion
            </button>
          )}
        </div>
        <div>
          {threads.map((thread) => (
            <div
              key={thread._id}
              className="border p-3 mb-2 rounded shadow cursor-pointer"
              onClick={() => {
                setSelectedThread(thread);
                setReplyModalOpen(true);
              }}
            >
              <h2 className="font-semibold">{thread.title}</h2>
              <p>{thread.content}</p>
              <small className="text-gray-600">by {thread.author}</small>
              <div className="mt-2">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleLikeThread(thread._id);
                  }}
                  className="bg-green-500 text-white p-1 rounded mr-2"
                >
                  Like ({thread.likes.length})
                </button>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDislikeThread(thread._id);
                  }}
                  className="bg-red-500 text-white p-1 rounded"
                >
                  Dislike ({thread.dislikes.length})
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Create Discussion Modal */}
      <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)}>
        <h2 className="text-xl font-bold mb-2">Create a New Discussion</h2>
        <form onSubmit={handleCreateThread}>
          <input
            type="text"
            placeholder="Thread Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-2 border rounded mb-2"
            required
          />
          <textarea
            placeholder="Thread Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className="w-full p-2 border rounded mb-2"
            required
          ></textarea>
          <button type="submit" className="bg-blue-500 text-white p-2 rounded w-full">
            Create
          </button>
        </form>
      </Modal>

      {/* Reply Modal */}
      <Modal isOpen={replyModalOpen} onRequestClose={() => setReplyModalOpen(false)}>
        {selectedThread && (
          <div>
            <h2 className="text-xl font-bold">{selectedThread.title}</h2>
            <p>{selectedThread.content}</p>
            <small className="text-gray-600">by {selectedThread.author}</small>
            <div className="mt-2">
              <button
                onClick={() => handleLikeThread(selectedThread._id)}
                className="bg-green-500 text-white p-1 rounded mr-2"
              >
                Like ({selectedThread.likes.length})
              </button>
              <button
                onClick={() => handleDislikeThread(selectedThread._id)}
                className="bg-red-500 text-white p-1 rounded"
              >
                Dislike ({selectedThread.dislikes.length})
              </button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default DiscussionForum;
