import React, { useEffect, useState } from "react";
import axios from "axios";
import Modal from "./AnnouncementModal"; // Import the Modal component
import Sidebar from "../../components/sidebar"; // Import the Sidebar component
import styles from '../../assets/Clubs.module.css'; // Import your CSS styles
import { useUser } from "../../context/UserContext"; // Import the UserContext

const Clubs = () => {
  const { user } = useUser(); // Get user from context
  const [clubs, setClubs] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [clubName, setClubName] = useState("");
  const [clubDescription, setClubDescription] = useState("");

  // Fetch userType from the user context
  const userType = user?.userType; // Assuming the role field in the user object represents the userType

  console.log("user", userType);

  useEffect(() => {
    const fetchClubs = async () => {
      try {
        const response = await axios.get("http://localhost:5000/clubs");
        setClubs(response.data);
      } catch (error) {
        console.error("Error fetching clubs:", error);
      }
    };

    fetchClubs();
  }, []);

  const handleCreateClub = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/clubs",
        {
          name: clubName,
          description: clubDescription,
          coordinator: user._id, // Assign the club coordinator as the current user
        },
        {
          headers: {
            Authorization: `Bearer ${user.token}`, // Include the token from context
          },
        }
      );
      setClubs((prev) => [...prev, response.data]);
      setIsModalOpen(false); // Close the modal after submission
      setClubName(""); // Clear the club name input
      setClubDescription(""); // Clear the club description input
    } catch (error) {
      console.error("Error creating club:", error);
    }
  };

  return (
    <div className={styles.dashboardContainer}>
      {/* Sidebar */}
      <div>
        <Sidebar userType={userType} userId={user?._id} />
      </div>

      {/* Main Content */}
      <div className={styles.mainContent}>
        <div className={styles.clubs}>
          <h1>Clubs</h1>
          <div className={styles.clubList}>
            {clubs.map((club) => (
              <div key={club._id} className={styles.clubCard}>
                <h3>{club.name}</h3>
                <p>{club.description}</p>
                <p>Coordinator: {club.coordinator?.name}</p> {/* Assuming coordinator has a name field */}
                <p>Date: {new Date(club.createdAt).toLocaleDateString()}</p>
              </div>
            ))}
          </div>

          {/* Conditionally render the "Create Club" button for club coordinators */}
          {userType === "clubCoordinator" && (
            <>
              <button
                className={styles.createButton}
                onClick={() => setIsModalOpen(true)}
              >
                Create Club
              </button>
              <Modal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSubmit={handleCreateClub}
                title="Create Club"
                inputFields={[
                  {
                    label: "Club Name",
                    type: "text",
                    value: clubName,
                    onChange: (e) => setClubName(e.target.value),
                  },
                  {
                    label: "Club Description",
                    type: "text",
                    value: clubDescription,
                    onChange: (e) => setClubDescription(e.target.value),
                  },
                ]}
              />
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Clubs;
