import React, { useEffect, useState } from "react";
import axios from "axios";
import Sidebar from "../sidebar";
import Modal from "react-modal";
import styles from "../../assets/Discussions.module.css";
import { useUser } from "../../context/UserContext";


const DiscussionForum = () => {
  const { user } = useUser();
  const [threads, setThreads] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedThread, setSelectedThread] = useState(null);
  const [replyModalOpen, setReplyModalOpen] = useState(false);


  useEffect(() => {
    fetchThreads();
  }, []);

  const fetchThreads = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/threads");
      setThreads(response.data);
    } catch (error) {
      console.error("Error fetching threads:", error);
    }
  };

  const handleLikeThread = async (threadId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/threads/${threadId}/like`,
        {},
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      setThreads((prevThreads) =>
        prevThreads.map((thread) =>
          thread.id === threadId ? { ...thread, likes: response.data.thread.likes } : thread
        )
      );
    } catch (error) {
      console.error("Error liking thread:", error);
    }
  };

  const handleDislikeThread = async (threadId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/threads/${threadId}/dislike`,
        {},
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      setThreads((prevThreads) =>
        prevThreads.map((thread) =>
          thread.id === threadId ? { ...thread, dislikes: response.data.thread.dislikes } : thread
        )
      );
    } catch (error) {
      console.error("Error disliking thread:", error);
    }
  };

  const handleCreateThread = async (e) => {
    e.preventDefault();
    try {
        const author=user.userName
        const authorId=user.id
      const response = await axios.post(
        "http://localhost:5000/threads",
        { title, content ,author,authorId},
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      console.log(response.data)
      setThreads([response.data.newThread, ...threads]);
      setTitle("");
      setContent("");
      setIsModalOpen(false);
    } catch (error) {
      console.error("Error creating thread:", error);
    }
  };

  return (
    <div className={styles.discussionContainer}>
      <Sidebar userType={user.userType} userId={user?._id} />
      <div className={styles.mainDiscussion}>
        <div className={styles.discussionHeader}>
          <h1>Discussion Forum</h1>
          {user && (
            <button
              onClick={() => setIsModalOpen(true)}
              className={styles.createButton}
            >
              Create Discussion
            </button>
          )}
        </div>
       <div className={styles.threadList}>
  {Array.isArray(threads) && threads.length > 0 ? (
    threads.map((thread) => (
      <div
        key={thread._id || Math.random()}
        className={styles.threadCard}
        onClick={() => {
          setSelectedThread(thread);
          setReplyModalOpen(true);
        }}
      >
        <h2>{thread.title}</h2>
        <p>{thread.content}</p>
        <small className={styles.threadAuthor}>by {thread.author}</small>
        <div className="mt-2">
          <button
            onClick={(e) => {
              e.stopPropagation();
              handleLikeThread(thread._id);
            }}
            className={styles.likeButton}
          >
            Like ({Array.isArray(thread.likes) ? thread.likes.length : 0})
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              handleDislikeThread(thread._id);
            }}
            className={styles.dislikeButton}
          >
            Dislike ({Array.isArray(thread.dislikes) ? thread.dislikes.length : 0})
          </button>
        </div>
      </div>
    ))
  ) : (
    <p>No discussions available.</p>
  )}
</div>


      </div>


      <Modal
  isOpen={isModalOpen}
  onRequestClose={() => setIsModalOpen(false)}
  className={styles.modalContent}
  overlayClassName={styles.modalOverlay}
>
  <div className={styles.modalContent}>
    <h2>Create a New Discussion</h2>
    <form onSubmit={handleCreateThread}>
      <div className={styles.formGroup}>
        <label>Thread Title</label>
        <input
          type="text"
          placeholder="Enter title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div className={styles.formGroup}>
        <label>Thread Content</label>
        <textarea
          placeholder="Enter content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        ></textarea>
      </div>
      <div className={styles.modalActions}>
        <button type="button" onClick={() => setIsModalOpen(false)}>
          Cancel
        </button>
        <button type="submit">Create</button>
      </div>
    </form>
  </div>
</Modal>;


      {/* Reply Modal */}
      <Modal isOpen={replyModalOpen} onRequestClose={() => setReplyModalOpen(false)}
        >
        {selectedThread && (
          <div className={styles.modalContent}>
            <h2>{selectedThread.title}</h2>
            <p>{selectedThread.content}</p>
            <small className={styles.threadAuthor}>by {selectedThread.author}</small>
            <div className="mt-2">
              <button
                onClick={() => handleLikeThread(selectedThread._id)}
                className={styles.likeButton}
              >
                Like ({selectedThread.likes.length})
              </button>
              <button
                onClick={() => handleDislikeThread(selectedThread._id)}
                className={styles.dislikeButton}
              >
                Dislike ({selectedThread.dislikes.length})
              </button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default DiscussionForum;
