const handleCreateClub = async (e) => {
  e.preventDefault();
  try {
    const clubData = {
      name: clubName,
      description: clubDescription,
      coordinator: user.userName,
    };

    // Convert image to Base64 if clubLogo is present
    if (clubLogo) {


       const options = {
        maxSizeMB: 1, // Maximum size in MB
        maxWidthOrHeight: 1024, // Maximum width or height
        useWebWorker: true, // Use a web worker for better performance
      };
      const compressedFile = await imageCompression(clubLogo, options);
      console.log(compressedFile.size)

      const base64Image = await new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onloadend = () => {
          const base64 = reader.result.split(",")[1]; // Get the Base64 string
          console.log(base64)
          resolve(base64);
        };
        reader.onerror = reject;
        reader.readAsDataURL(compressedFile); // Convert image to Base64
      });
      clubData.logo = base64Image; // Add Base64 string to the payload
    }
    console.log(clubData)
    // Make the POST request
    const response = await axios.post("http://localhost:5000/clubs", clubData, {
      headers: {
        Authorization: `Bearer ${user.token}`,
        "Content-Type": "application/json",
      },
    });

    console.log(response)
    // Update state and reset form
    setClubs((prev) => [...prev, response.data]);
    setIsModalOpen(false);
    setClubName("");
    setClubDescription("");
    setClubLogo(null);
  } catch (error) {
    console.error("Error creating club:", error);
    alert("Failed to create club. Please try again later.");
  }
};

  const handleJoinClub = async (clubId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/clubs/${clubId}/join`,
        {},
        {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        }
      );

      // Update the clubs state with the updated club data
      setClubs((prevClubs) =>
        prevClubs.map((club) =>
          club._id === clubId ? response.data : club
        )
      );
    } catch (error) {
      console.error("Error joining club:", error);
    }
  };

  return (
    <div className={styles.dashboardContainer}>
      {/* Sidebar */}
      <div>
        <Sidebar userType={userType} userId={user?._id} />
      </div>

      {/* Main Content */}
      <div className={styles.mainContent}>
        <div className={styles.clubs}>
          <h1>Clubs</h1>
          <div className={styles.clubList}>
            {clubs.map((club) => (
              <div key={club._id} className={styles.clubCard}>
                  {club.logo && (
                    <img
                      src={`data:image/png;base64,${club.logo}`} // Display Base64 image
                      // alt={club.name}
                      alt="hello"
                      className={styles.clubLogo}
                    />
                  )}
                <h3>{club.name}</h3>
                <p>{club.description}</p>
                <p>Coordinator: {club?.coordinator}</p> {/* Assuming coordinator has a name field */}
                <p>Members: {club.members?.length || 0}</p> {/* Display number of members */}
                <p>Date: {new Date(club.createdAt).toLocaleDateString()}</p>

                {/* Conditionally render the "Join Club" button for students */}
                {userType === "student" && !club.members?.includes(user._id) && (
                  <button
                    className={styles.joinButton}
                    onClick={() => handleJoinClub(club._id)}
                  >
                    Join Club
                  </button>
                )}
              </div>
            ))}
          </div>

          {/* Conditionally render the "Create Club" button for club coordinators */}
          {userType === "clubCoordinator" && (
            <>
              <button
                className={styles.createButton}
                onClick={() => setIsModalOpen(true)}
              >
                Create Club
              </button>
              <Modal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSubmit={handleCreateClub}
                title="Create Club"
                inputFields={[
                  {
                    label: "Club Name",
                    type: "text",
                    value: clubName,
                    onChange: (e) => setClubName(e.target.value),
                  },
                  {
                    label: "Club Description",
                    type: "text",
                    value: clubDescription,
                    onChange: (e) => setClubDescription(e.target.value),
                  },
                  {
                    label: "Club Logo",
                    type: "file",
                    onChange: (e) => setClubLogo(e.target.files[0]), // Handle file upload
                  },
                ]}
              />
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Clubs;
