// SelectUserType.js
import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useHistory } from 'react-router-dom';

const SelectUserType = () => {
  const [userType, setUserType] = useState('');
  const location = useLocation();
  const history = useHistory();
  const email = new URLSearchParams(location.search).get('email');
  const name = new URLSearchParams(location.search).get('name');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/auth/save-user-type', {
        email,
        userType,
        name,
      });
      // Redirect or handle successful signup
      const token = response.data.token; // Assuming your backend returns a token
      localStorage.setItem('token', token); // Store token in local storage
      history.push('/'); // Redirect to home or dashboard
    } catch (error) {
      console.error(error);
      alert('Error saving user type');
    }
  };

  return (
    <div>
      <h1>Welcome, {name}</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="userType">Select User Type:</label>
        <select
          id="userType"
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
          required
        >
          <option value="">Select...</option>
          <option value="student">Student</option>
          <option value="alumni">Alumni</option>
          <option value="faculty">Faculty</option>
          <option value="club_coordinator">Club Coordinator</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default SelectUserType;
