import React, { useEffect, useState, useContext } from "react";
import { UserContext } from "../context/UserContext"; // Assuming UserContext is set up
import axios from "axios";

const ProfilePage = () => {
  const { user } = useContext(UserContext); // Get user ID from context
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    if (user?.id) {
      fetchUserData(user.id);
    }
  }, [user]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:5000/users/${userId}`);
      setUserData(response.data);
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  if (!userData) return <p>Loading...</p>;

  return (
    <div className="max-w-lg mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <div className="text-center">
        <img
          src={userData.avatar || "https://via.placeholder.com/150"}
          alt="User Avatar"
          className="w-32 h-32 mx-auto rounded-full border"
        />
        <h2 className="text-2xl font-semibold mt-4">{userData.name}</h2>
        <p className="text-gray-600">{userData.email}</p>
        <p className="text-sm text-blue-500 font-medium">{userData.userType.toUpperCase()}</p>
      </div>
    </div>
  );
};

export default ProfilePage;
