import React, { useEffect, useState } from "react";
import axios from "axios";
import Sidebar from "../sidebar"; // Import your existing Sidebar

const DiscussionForum = () => {
  const [threads, setThreads] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    fetchThreads();
  }, []);

  const fetchThreads = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/threads");
      setThreads(response.data);
    } catch (error) {
      console.error("Error fetching threads:", error);
    }
  };

  const handleCreateThread = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/api/threads",
        { title, content },
        {
          headers: { Authorization: `Bearer ${user.token}` },
        }
      );
      setThreads([response.data.thread, ...threads]);
      setTitle("");
      setContent("");
    } catch (error) {
      console.error("Error creating thread:", error);
    }
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 p-6 ml-64">
        <h1 className="text-2xl font-bold mb-4">Discussion Forum</h1>

        {/* Create Thread Form */}
        {user && (
          <form onSubmit={handleCreateThread} className="mb-4">
            <input
              type="text"
              placeholder="Thread Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full p-2 border rounded mb-2"
              required
            />
            <textarea
              placeholder="Thread Content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              className="w-full p-2 border rounded mb-2"
              required
            ></textarea>
            <button type="submit" className="bg-blue-500 text-white p-2 rounded w-full">
              Create Thread
            </button>
          </form>
        )}

        {/* Display Threads */}
        <div>
          {threads.map((thread) => (
            <div key={thread._id} className="border p-3 mb-2 rounded shadow">
              <h2 className="font-semibold">{thread.title}</h2>
              <p>{thread.content}</p>
              <small className="text-gray-600">by {thread.author}</small>

              <div className="mt-2">
                <button
                  onClick={() => handleLikeThread(thread._id)}
                  className="bg-green-500 text-white p-1 rounded mr-2"
                >
                  Like ({thread.likes.length})
                </button>
                <button
                  onClick={() => handleDislikeThread(thread._id)}
                  className="bg-red-500 text-white p-1 rounded"
                >
                  Dislike ({thread.dislikes.length})
                </button>
              </div>

              <a
                href={`/threads/${thread._id}`}
                className="text-blue-500 hover:underline block mt-2"
              >
                View Replies
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DiscussionForum;
