import React, { useState } from "react";
import axios from "axios";
import '../assets/signup.module.css'
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const [formData, setFormData] = useState({ name: "", email: "", password: "", userType: "student" });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const navigate=useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message

    try {
      const res = await axios.post("http://localhost:5000/auth/signup", formData);
      alert(res.data.message);
      navigate("/login")
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  const handleGoogleSignup = async () => {
    window.open("http://localhost:5000/auth/google", "_self");
  };

  return (
    <div className="signUpContainer">
      <h1 className="signUpHeading">Signup</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSignup} className="singUpForm">
        <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />

        <label>
          User Type:
          <select name="userType" value={formData.userType} onChange={handleChange}>
            <option value="student">Student</option>
            <option value="alumni">Alumni</option>
          </select>
        </label>

        <button type="submit">Signup</button>
      </form>
      <h5>or</h5>
      <button onClick={handleGoogleSignup}>Signup with Google</button>
      <p>Already had an account <a href="/login">login</a></p>
    </div>
  );
};

export default Signup;
