import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../assets/signup.module.css'

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "", userType: "student" });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const navigate=useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message

    try {
        const res = await axios.post("http://localhost:5000/auth/login", formData);
        alert(res.data.message); // Ensure message exists

        navigate("/")
    } catch (error) {
        if (error.response && error.response.data && error.response.data.message) {
            alert(error.response.data.message); // Show specific error message
        } else {
            alert("An unexpected error occurred."); // Fallback message
        }
    }
};

  const handleGoogleSignup = async () => {
    window.open("http://localhost:5000/auth/google", "_self");
  };

  return (
    <div className="signUpContainer">
      <h1 className="signUpHeading">Login</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleLogin} className="signUpForm">
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />

        <label className="signUpLabel">
          User Type:
          <select name="userType" value={formData.userType} onChange={handleChange}>
            <option value="student">Student</option>
            <option value="alumni">Alumni</option>
            <option value="alumni">Faculty</option>
            <option value="alumni">club_coordinator</option>
            <option value="alumni">Admin</option>
          </select>
        </label>

        <button type="submit" className="singUpButton">Login</button>
      </form>
      <h5>or</h5>
      <button onClick={handleGoogleSignup} className="signUpButton">Login with Google</button>
      <p>Create Account <a href="/signup">signup</a></p>
    </div>
  );
};

export default Login;
